DECLARE @WantedCharacters nvarchar(100) = N'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';

SELECT
    OriginalString    = v.OriginalString
   ,TransformedString = REPLACE(TRANSLATE(v.OriginalString, REPLACE(TRANSLATE(v.OriginalString, @WantedCharacters, REPLICATE('a', LEN(@WantedCharacters))), 'a', ''), REPLICATE('0', LEN(REPLACE(TRANSLATE(v.OriginalString, @WantedCharacters, REPLICATE('a', LEN(@WantedCharacters))), 'a', '')))), '0', '')
FROM (
    VALUES (N'Add sample strings in this values list.')
       ,(N'abc1234def5678ghi90jkl#@$&')
       ,(N'1234567890$@çˆ±')
       ,(N'JAHDBESBN%*#*@*($E*sd55bn')
       ,(N'Some text, other text.  ''.,!?/<>"[]{}|`~@#$%^&*()-+=/\:;

After a CR/LF!' + +CHAR(13) + CHAR(10) + CHAR(9) + N'After CR/LF & tab character.')
) AS v (OriginalString);
